import React, { useState, useEffect } from 'react';
import './Navbar.css';

export default function Navbar() {
    const [scrolled, setScrolled] = useState(false); // Stato per lo sfondo della navbar

    // Gestore dello scroll
    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 150); // Cambia stato oltre i 150px
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const [menuActive, setMenuActive] = useState(false);

    const toggleMenu = () => {
        setMenuActive(!menuActive);
    };



    return (
        <nav className={`navbar ${scrolled ? 'navbar-scrolled' : ''}`}>
            {/* Logo */}
            <div className="navbar-logo">MARCO NITTO</div>

            {/* Menu Toggle per mobile */}
            <div className="navbar-toggle" onClick={toggleMenu}>
                â˜°
            </div>

            {/* Bottoni */}
            <div className={`navbar-buttons ${menuActive ? 'active' : ''}`}>
                <button className="navbar-button">Home</button>
                <button className="navbar-button">About</button>
                <button className="navbar-button">I Miei Servizi</button>
                <button className="navbar-button">Blog</button>
                <button className="navbar-button">Contattami</button>
            </div>
        </nav>
    );
}
