import React from "react";
import "./Header.css";
import Navbar from "../Navbar/Navbar";
import SplitText from "../SplitText/SplitText";
import { easings } from '@react-spring/web';
import ShinyText from "../ShinyText/ShinyText";
import FadeContent from "../FadeContent/FadeContent";
import RotatingText from "../RotatingText/RotatingText";
import AnimatedContent from "../AnimatedContent/AnimatedContent";

type HeaderProps = {
  backgroundImage: string;
  title: string;
  subtitle: string;
  buttonText: string;
  onButtonClick: () => void;
};

export default function HeaderComponent({
  backgroundImage,
  title,
  subtitle,
  buttonText,
  onButtonClick,
}: HeaderProps) {

  const handleAnimationComplete = () => {
    console.log("animated")
  }

  return (
    <header
      className="header-component"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >

<AnimatedContent
            distance={150}
            direction="vertical"
            reverse={false}
            config={{ tension: 80, friction: 20 }}
            initialOpacity={0.2}
            animateOpacity
            scale={1.1}
            threshold={0.2}
        >
      <Navbar></Navbar>

      </AnimatedContent>


      <div className="header-title header-content">

        <SplitText
          text={title}
          className="text-2xl font-semibold text-center"
          delay={50}
          animationFrom={{ opacity: 0, transform: 'translate3d(0,50px,0)' }}
          animationTo={{ opacity: 1, transform: 'translate3d(0,0,0)' }}
          easing={easings.easeOutCubic}
          threshold={0.2}
          rootMargin="-50px"
          onLetterAnimationComplete={handleAnimationComplete}
        />

        <p className="header-subtitle"></p>


        {/* <FadeContent blur={true} duration={1000} easing="ease-out" initialOpacity={0}>
          <button className="header-button" onClick={onButtonClick}>
            <ShinyText text={buttonText} disabled={false} speed={3} className='custom-class' />
          </button>
        </FadeContent> */}

<AnimatedContent
            distance={150}
            direction="vertical"
            reverse={false}
            config={{ tension: 80, friction: 20 }}
            initialOpacity={0.2}
            animateOpacity
            scale={1.1}
            threshold={0.2}
        >

          <div className="row">
            <p className="col-6 ml">CONTINUA A </p>
          <RotatingText
          texts={['COMBATTERE','CREDERCI', 'MIGLIORARE', 'CONQUISTARE']}
          mainClassName="px-2 sm:px-2 md:px-3 bg-cyan-300 text-black overflow-hidden py-0.5 sm:py-1 md:py-2 justify-center rounded-lg rota col-6"
          staggerFrom={"last"}
          initial={{ y: "100%" }}
          animate={{ y: 0 }}
          exit={{ y: "-120%" }}
          staggerDuration={0.025}
          splitLevelClassName="overflow-hidden pb-0.5 sm:pb-1 md:pb-1"
          transition={{ type: "spring", damping: 30, stiffness: 400 }}
          rotationInterval={2000}
        /> 
          </div>
          </AnimatedContent>

      </div>
    </header>
  );
}
