import React from 'react';
import './Footer.css';
import SimpleCard from '../Card/SimpleCard';
import AnimatedContent from '../AnimatedContent/AnimatedContent';
import LogoWall from '../LogoWall/LogoWall';
import SectionTitle from '../SectionTitle/SectionTitle';

interface FooterItemProps {
    imageUrl: string;
    text: string;
}

interface FooterProps {
    bottomImageUrl: string;
    bottomText: string;
    logowall: string;
}

export default function Footer({ bottomImageUrl, bottomText, logowall }: FooterProps) {



    const logoImgs = [
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" },
        { imgUrl: logowall, altText: "Logo" }
    ];

    return (

        <>

            <AnimatedContent
                distance={150}
                direction="vertical"
                reverse={false}
                config={{ tension: 80, friction: 20 }}
                initialOpacity={0.2}
                animateOpacity
                scale={1.1}
                threshold={0.2}
            >


                <SectionTitle text="Contatti" />

                <br />


                <footer className="footer">

                    <div className='row'>

                        <div className='col-sm'>
                            <SimpleCard
                                title="DOVE MI ALLENO"
                                children={

                                    <iframe src="https://www.google.com/maps/embed?pb=!1m23!1m12!1m3!1d96815.58718897021!2d14.687886745303384!3d40.68526870884758!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!4m8!3e6!4m0!4m5!1s0x133bc2349aa5014f%3A0x73b7127729feca0b!2sVia%20Luigi%20de%20Marzia%2C%207%2C%2084126%20Salerno%20SA!3m2!1d40.6852935!2d14.770294999999999!5e0!3m2!1sit!2sit!4v1733866020383!5m2!1sit!2sit"
                                        width="250"
                                        height="100"
                                        loading="lazy">
                                    </iframe>
                                }
                            />

                        </div>


                        <div className='col-sm margin'>

                            <SimpleCard

                                title="I MIEI SOCIAL"
                                children={
                                    <>
                                        <div className='row'>
                                            <i className="bi bi-instagram footer-first-section-image col-sm"><a href="https://www.instagram.com/marcus_tank_pt/"></a></i>
                                        </div>

                                        <div className='row'>
                                            <i className="bi bi-tiktok footer-first-section-image col-sm"> </i>
                                        </div>
                                    </>
                                }
                            />




                        </div>

                    </div>


                    <br></br>

                    <div className="footer-bottom">
                       {/*  {<img src={bottomImageUrl} alt="Footer bottom" className="footer-bottom-image" />} */}
                        <p className="footer-bottom-text">{bottomText}</p>
                    </div>
                </footer>

            </AnimatedContent>


            <div style={{ height: '600px', width: '100%', position: 'relative' }}>
                <LogoWall
                    items={logoImgs}
                    direction='horizontal'
                    pauseOnHover={false}
                    size='clamp(8rem, 1rem + 20vmin, 25rem)'
                    duration='60s'
                    bgColor='#060606'
                    bgAccentColor='#111111'
                />
            </div>

        </>
    );
}
