import React from 'react';
import './NoteLegCo.css';
import AnimatedContent from '../AnimatedContent/AnimatedContent';


interface AboutMeProps {
    imageSrc: string;
    altText: string;
    htmlContent: string; // HTML content as a string
}

export default function NoteLegCo({ htmlContent }: AboutMeProps) {
    return (
        <AnimatedContent
            distance={150}
            direction="vertical"
            reverse={false}
            config={{ tension: 80, friction: 20 }}
            initialOpacity={0.2}
            animateOpacity
            scale={1.1}
            threshold={0.2}
        >
            <div className="nt">
                <div className="nt-text" dangerouslySetInnerHTML={{ __html: htmlContent }} />
            </div>

        </AnimatedContent>
    );
}
